package nov.automation.week2;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=1; i<=10; i++) {
			if (i%2==0) {
				System.out.println(i); 
				System.out.println("Even number");
			} else {
				System.out.println(i); 
				System.out.println("Odd number");
				
			}
	    }
		
	}
	
	}
